mapper.select:
select
	count(1) totalCount
from 
	carexx_acl_role


    	creator = #{creator,jdbcType=INTEGER}


    	and name = #{name,jdbcType=VARCHAR}


    	and status = #{status,jdbcType=TINYINT}

select

from 
	carexx_acl_role


    	creator = #{creator,jdbcType=INTEGER}


    	and name = #{name,jdbcType=VARCHAR}


    	and status = #{status,jdbcType=TINYINT}


order by
	create_time desc
limit ${rowIndex}, ${pageSize}

mapper.insert:
insert into carexx_acl_role (
	name, status, remark, create_time, creator
)
values (
	#{name,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, 
	#{remark,jdbcType=VARCHAR}, now(), #{creator,jdbcType=INTEGER}
)
