mapper.select:
SELECT count(1) totalCount FROM carexx_care_inst
inst_name like CONCAT('%', '${instName}', '%')
and inst_status = #{instStatus,jdbcType=VARCHAR}
and inst_type = #{instType,jdbcType=VARCHAR}

SELECT id id, inst_type instType, inst_name instName, inst_status instStatus, inst_region instRegion, inst_addr instAddr, inst_lng instLng, inst_lat instLat, introduce introduce, create_time createTime, modify_time modifyTime FROM carexx_care_inst
inst_name like CONCAT('%', '${instName}', '%')
and inst_status = #{instStatus,jdbcType=VARCHAR}
and inst_type = #{instType,jdbcType=VARCHAR}
ORDER BY createTime DESC limit ${rowIndex}, ${pageSize}
