mapper.select:
SELECT

FROM
	carexx_care_service
WHERE
	work_type_id = #{workTypeId,jdbcType=INTEGER}
	and service_name = #{serviceName,jdbcType=VARCHAR}

SELECT count(1) totalCount FROM carexx_care_service cs
cs.service_name like CONCAT('%', '${serviceName}', '%')
and cs.work_type_id = #{workTypeId,jdbcType=INTEGER}
and cs.service_status = #{serviceStatus,jdbcType=TINYINT}

SELECT cs.id id, cs.service_name serviceName, cs.work_type_id workTypeId, wt.work_type_name workTypeName, cs.service_name serviceName, cs.service_status serviceStatus, cs.create_time createTime, cs.modify_time modifyTime FROM carexx_care_service cs LEFT JOIN carexx_work_type wt ON wt.id = cs.work_type_id
cs.service_name like CONCAT('%', '${serviceName}', '%')
and cs.work_type_id = #{workTypeId,jdbcType=INTEGER}
and cs.service_status = #{serviceStatus,jdbcType=TINYINT}
ORDER BY cs.create_time DESC LIMIT ${rowIndex}, ${pageSize}
